INSERT INTO `career` (`id`, `content`) VALUES
(1, '<p><strong>Job Title: </strong>Geophysicist B.Tech + Electrical / Electronic Engineer with GPR experience<br><strong>Job Location:</strong> Noida &amp; Abroad<br><strong>Expected Skill-set: </strong>Knowledge of GPR &amp; RADAN or any other GPR Software.<br><strong>Education Qualification:</strong> B.Tech. (Geophysics), M.Tech.(Applied Geophysics)<br><strong>Years of Experience:</strong> 1 to 5 years. Fresher can also apply. Passport is MUST.<br><strong>Remuneration:</strong> Depend on Experience and Knowledge of Candidate.<br><strong>Job Description:</strong> Perform high quality interpretation of 2D and 3D and analysis of GPR observed data about underground utilities etc.<br>&nbsp;</p>'),
(2, '<p><strong>Job Title: </strong>GPR Operator<br><strong>Job Location:</strong> Noida &amp; Abroad<br><strong>Expected Skill-set: </strong>Knowledge of GPR handling.<br><strong>Education Qualification:</strong> B.Sc. in Geophysics<br><strong>Years of Experience:</strong> 1 to 3 years. Fresher can also apply.Passport is MUST.<br><strong>Remuneration:</strong> Depend on Experience and Knowledge of Candidate.<br><strong>Job Description:</strong> Survey of Under Ground Utility by using Ground-penetrating Radar (GPR) .<br>&nbsp;</p>'),
(3, '<p><strong>Job Title: </strong>Field Surveyor&nbsp;<br><strong>Job Location:</strong> Noida &amp; Any where in India.<br><strong>Expected Skill-set: </strong>Able to read, write &amp; understand English.<br><strong>Education Qualification:</strong>Minimum 12th<br><strong>Years of Experience:</strong> 1 to 2 year , Fresher can also apply.&nbsp;<br><strong>Remuneration:</strong> 7k ctc (All inclusive).<br><strong>Other Benefits:</strong> Conveyance Allowances (from office till project site), Accommodation, Food Allowances (Rs75 per day for outside NCR)<br><strong>Job Description:</strong> Collection of POI (Point of Interest), APT (Address Point) by using different&nbsp;<br>Tools (Paper Map, PDA, GPS device)/ Route Survey for fiber optics. This is purely a field job.<br>&nbsp;</p>'),
(4, '<p><strong>Job Title: </strong>GIS Executive<br><strong>Job Location:</strong> Noida<br><strong>Expected Skill-set: </strong>Arc GIS, MapInfo, Auto CAD,&nbsp;<br><strong>Education Qualification:</strong> Graduate/ PG in GIS &amp; RS and equitant.<br><strong>Years of Experience:</strong> 2 to 3 years, Fresher can also apply.<br><strong>Remuneration:</strong> Depend on Experience and Knowledge of Candidate.<br><strong>Job Description:</strong> Ability to work on Arc GIS, MapInfo &amp; Auto CAD Correction and integration into existing mapping products.<br>&nbsp;Data Attribution , Geo referencing, Data Collection &amp; the database preferred Data inter-operation with existing data/maps.<br>&nbsp;Ability to complete tasks within given time frame. Ability to adjust to changing priorities and meet deadlines.&nbsp;<br>Ability to work independently and as part of a team.<br>&nbsp;</p>'),
(5, '<p><strong>Job Title: </strong>CAD Operator<br><strong>Job Location:</strong> Noida<br><strong>Expected Skill-set: </strong>Auto CAD.<br><strong>Education Qualification:</strong> Graduate<br><strong>Years of Experience:</strong> 2 to 3 years, Fresher can also apply.<br><strong>Remuneration:</strong> Depend on Experience and Knowledge of Candidate.<br><strong>Job Description:</strong> Ability to work on Auto CAD Land base, Digitization of image, topology Data Attribution ,<br>&nbsp;Geo referencing, Data Collection &amp; the database preferred Coordinate the use of GPS field data and mapping<br>&nbsp;systems Ability to adjust to changing priorities and meet deadlines. Ability to work independently and as part of a team. .<br>&nbsp;</p>');
